mapper.select:
select id, tool, logtype, description, collecttype as collectionType, cinterval as interval, ownerid as owner, expired, tscreated as created, tsstart as start, tsend as end, tslastcollect as lastCollect, tsnext as nextAction, tslastpoint as lastPoint from rsss."collection_plan" as t_collect where expired is false and tsnext is not null
order by t_collect.tsnext
limit #{limit}

mapper.update:
update rsss."collection_plan" set
    tslastcollect = #{lastCollect},
    tsnext = #{nextAction},
    expired = #{expired},
    tslastpoint = #{lastPoint}
where id = #{id}
